$(document).ready(function(){
    Parallax();
    CarouselPassagens();
 
    function Parallax(){
        $('.call_to_action').each(function(){
            var $obj = $(this);
            $(window).scroll(function() {
                var yPos = -($(window).scrollTop() / $obj.data('speed')); 
                var bgpos = '50% '+ yPos + 'px';
                $obj.css('background-position', bgpos );
            }); 
        });
    }

    function CarouselPassagens(){
        //Inicia o carrousel
        var owl_pac = jQuery('.owl-carousel');
        owl_pac.owlCarousel({
            margin: 10,
            autoWidth:false,
            loop: false,
            center: false,
            nav:false,
        })

        $('#menu-sp').click(function(){  
                $('#saopaulo').fadeIn();
                $('.pacotes').not( document.getElementById( "saopaulo" ) ).hide();
                $('#menu-sp span').addClass('hoverOption');
                $('#menu-orlando span').removeClass('hoverOption');
                
        });
        $('#menu-orlando').click(function(){  
                $('#orlando').fadeIn();
                $('.pacotes').not( document.getElementById( "orlando" ) ).hide();
                $('#menu-sp span').removeClass('hoverOption');
                $('#menu-orlando span').addClass('hoverOption');
                
        });  
    }
    $(function() {
        $(window).scroll( function(){
        
        
            $('.fadeInBlock').each( function(i){
                
                var bottom_of_object = $(this).position().top + $(this).outerHeight();
                var bottom_of_window = $(window).scrollTop() + $(window).height();
                
                /* Adjust the "200" to either have a delay or that the content starts fading a bit before you reach it  */
                bottom_of_window = bottom_of_window + 200;  
            
                if( bottom_of_window > bottom_of_object ){
                    
                    $(this).animate({'opacity':'1'},2000);
                        
                }
            }); 
        
        });
    });
});
